	(7,1,3,'index','y','js','function addBirdToText() {\n  jQuery(\'.twitter-item p\').before(\'<span class=\"tw-bird-icon\"></span>\');\n}\n\nfunction rotateGwItems() {\n  $(\'#rotator-cont\').cycle({\n    prev: \'#previous-slide\',\n    next: \'#next-slide\',\n    timeout: 0\n  });\n  $(\'#rotator-pager\').on(\'click\',function(e){\n    e.preventDefault();\n  });\n}\n\nfunction toggleOverlay() {\n\n  // below are helper functions to disable scrolling\n  // left: 37, up: 38, right: 39, down: 40,\n  // spacebar: 32, pageup: 33, pagedown: 34, end: 35, home: 36\n  var keys = [37, 38, 39, 40];\n\n  function preventDefault(e) {\n    e = e || window.event;\n    if (e.preventDefault)\n    e.preventDefault();\n    e.returnValue = false;\n  }\n\n  function keydown(e) {\n      for (var i = keys.length; i--;) {\n          if (e.keyCode === keys[i]) {\n              preventDefault(e);\n              return;\n          }\n      }\n  }\n\n  function wheel(e) {\n    preventDefault(e);\n  }\n\n  function disable_scroll() {\n    if (window.addEventListener) {\n        window.addEventListener(\'DOMMouseScroll\', wheel, false);\n    }\n    window.onmousewheel = document.onmousewheel = wheel;\n    document.onkeydown = keydown;\n  }\n\n  function enable_scroll() {\n      if (window.removeEventListener) {\n          window.removeEventListener(\'DOMMouseScroll\', wheel, false);\n      }\n      window.onmousewheel = document.onmousewheel = document.onkeydown = null;\n  }\n  // end helper functions\n\n  $(\'#slide5 .text-layout-explore-interests\').on(\'click\', function(event) {\n    disable_scroll();\n    $(\'#only-gw-overlay\').removeClass(\'inactive\').addClass(\'active\').show();\n  });\n\n  $(\'#only-gw-overlay .close\').on(\'click\', function(e){\n    enable_scroll();\n    $(\'#only-gw-overlay\').removeClass(\'active\').addClass(\'inactive\');\n  });\n}\n\n// we need to deal with many screen widths and this function does so\nfunction resizeBackgroundImages() {\n  var selections = [\n  